const scrollToTopButton=document.getElementById("js-top"),scrollFunc=()=>{let y=window.scrollY;scrollToTopButton.className=y>0?"top-link show":"top-link hide"};window.addEventListener("scroll",scrollFunc);const scrollToTop=()=>{const c=document.documentElement.scrollTop||document.body.scrollTop;c>0&&(window.requestAnimationFrame(scrollToTop),window.scrollTo(0,c-c/5))};scrollToTopButton.onclick=function(e){e.preventDefault(),scrollToTop()};